import React, {Component} from 'react';
import './Body.css';

class Body extends Component {
  constructor(){
    super();

    this.state = {
      slideToShow: 0
    };

    //this.handleLogin = this.handleLogin.bind(this);
  }

  getMinSlide() {
    return 0;
  }

  getMaxSlide() {
    return 2;
  }

  handleLeft() {
    this.setState({
      login: true
    });
  }

  handleRight() {
    this.setState({
      login: true
    });
  }

  render() {
    return (
      <div className="Body">
            <div className="Slideshow">
                <a className="LeftArrow" />
                <a className="RightArrow" />
                <div className="Content Show">
                    <div className="Description">
                        Apple
                    </div>
                    <img src={require('./img/apple.jpeg')} />
                </div>
                <div className="Content">
                    <div className="Description">
                        Orange
                    </div>
                    <img src={require('./img/orange.jpeg')} />
                </div>
                <div className="Content">
                    <div className="Description">
                        Banana
                    </div>
                    <img src={require('./img/banana.jpeg')} />
                </div>
            </div>
        </div>
    );
  }
}

export default Body;
