import React, {Component} from 'react';
import './Body.css';

class Body extends Component {
  constructor(){
    super();

    this.state = {
      slideToShow: 1
    };

    //this.handleLogin = this.handleLogin.bind(this);
  }

  getMinSlide() {
    return 1;
  }

  getMaxSlide() {
    return 3;
  }

  handleLeft() {
    var newSlide = this.state.slideToShow--
    if (newSlide >= this.getMinSlide()) {
        this.setState({
          slideToShow: newSlide
        });
    }

  }

  handleRight() {
    var newSlide = this.state.slideToShow;
    if (newSlide >= this.getMaxSlide()) {
        this.setState({
          slideToShow: newSlide
        });
    }
  }

  render() {
    return (
        <div className="Body">
            <div className="Slideshow">
                <a className="LeftArrow" onClick={this.handleLeft} />
                <a className="RightArrow" onClick={this.handleRight} />
                <div className={this.state.slideToShow == 1 ? "Content Show" : "Content"} >
                    <div className="Description">
                        Apple
                    </div>
                    <img src={require('./img/apple.jpeg')} />
                </div>
                <div className={this.state.slideToShow == 2 ? "Content Show" : "Content"} >
                    <div className="Description">
                        Orange
                    </div>
                    <img src={require('./img/orange.jpeg')} />
                </div>
                <div className={this.state.slideToShow == 3 ? "Content Show" : "Content"} >
                    <div className="Description">
                        Banana
                    </div>
                    <img src={require('./img/banana.jpeg')} />
                </div>
            </div>
        </div>
    );
  }
}

export default Body;
