import React, {Component} from 'react';
import './Header.css';
import Login from './Login'
import Model from './utils/Model'
import RightNav from './utils/RightNav'

const Header = () => { 
    return (
        <div className="Header">
            <div className="Left">
                <Menu />
            </div>
            <div className="Right">
                <User />
            </div>
        </div>
    );
}

class Menu extends Component {
    constructor(props) {
        super(props);

        this.state = {
            showNav : false
        };

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick = () => {
        this.setState({
            showNav : !state.showNav
        });
        /*document.querySelector(".RightNav").classList.add("Show");*/
    }

    render() {
        return (
            <div>
                <a className="Menu" onClick={this.handleClick} />
                <RightNav displayState={this.state.showNav} />
             </div>
        );
    }
}

class User extends Component {
    constructor(props) {
        super(props);

        this.state = {
            clicked : false
        };
    }

    handleClick = () => {
        this.setState({
            clicked : true
        });
    }

    render() {
        return (
            <div>
                <a className="User" onClick={this.handleClick} />
                {this.state.clicked ? <Model content={<Login />} /> : null}
             </div>
        );
    }
}

export default Header;
